//

const WorkPage = ( ) => {

    // captures the current URL
    const itemIDObject = useParams(useLocation());
    const itemID = parseInt(itemIDObject.id - 1);

    const workItem = Works[itemID];
    console.log(workItem);

  return (
    <>
    <Header />
    <div className="h-screen w-screen flex flex-col align-center justify-end">
    <h1>{workItem.title}</h1>
    </div>
    </>
  )
}
export default WorkPage
