import { Works } from '../data';

import '../styles/main.css';

const HomePortfolio = () => {
  return (

    <section class="h-screen flex flex-col justify-center bg-white" data-section="bg-white">
        <heading class="h-8 w-screen text-center text-2xl lg:text-4xl lg:mb-8 mb-4">PORTFOLIO</heading>
            <div class="work-display h-2/3 lg:h-1/2 w-auto grid grid-rows-2 grid-cols-2 gap-4 mx-4
            md:mx-20 lg:grid-rows-1 lg:grid-cols-4">
                {Works.map((workItem) => {
                    const { id, img1, title } = workItem

                    const img = {
                        backgroundImage: 'url(' + img1 + ')',
                    };
                    return (
                        <div 
                        key={id} 
                        onclick="location.href='work-kernagis.html'" 
                        className="cursor-pointer border-2 lg:border-4 border-black flex bg-cover bg-top"
                        style={img}>
                    <button className="border-t-2 lg:border-t-4 border-black h-8 lg:h-12 w-full font-bold self-end bg-white text-xs 
                    lg:text-base">{title}</button>
                        </div>
                    );
                })}
            </div>
    </section>

  );
}
export default HomePortfolio
