import '../styles/main.css';
import '../styles/tailwind.css';

// import { useEffect } from 'react';
import { useState } from 'react';

const test = () => {
    console.log('test');
}

const Header = () => {

    const [state, setState] = useState("");
    const toggle = () => {
        setState(state === "" ? "hidden" : "w-full h-screen");
        console.log('clicked');
    }

  return (

    <header 
    className="w-full h-16 md:h-20 bg-transparent fixed z-50 flex justify-between lg:h-[7rem]" 
    data-header>
        <div className="md:px-0 h-full w-full mx-4 flex justify-between items-center">

            {/* <!-- Medium resolution upwards contact info --> */}
            <ul className="hidden md:flex md:flex-col w-50 md:w-[250px]">
                <li className="py-2 flex">
                    <div id="email" className="bg-contain w-5 h-5 mt-1 bg-[url('../images/email_white.png')]" data-email></div>
                    <a href="mailto:augustinas.pietaris@gmail.com"><span className="mt-1 mx-3 text-sm">augustinas.pietaris@gmail.com</span></a>
                </li>
                <li className="py-2 flex md:-mt-2">
                    <div id="phone" className="bg-[url('../images/phone-call_white.png')] bg-contain w-5 h-5 mt-1" data-phone></div>
                    <a href="tel:+37068716994"><span className="text-sm mt-2 mx-3">+370 687 169 94</span></a>
                </li>
            </ul>

            {/* <!-- Logo Here --> */}
            <a href="index.html" className="logo bg-[url('../images/logo.png')] h-9 w-[120px] bg-no-repeat bg-contain z-50
            md:w-[180px] md:h-[54px]
            lg:w-[270px] lg:h-[80px]" data-logo></a>

            {/* <!-- medium resolution and upwards navigation --> */}
            <ul className="hidden md:flex md:w-[250px] md:flex-col md:text-right">
                <li className="py-2"><a className="text-md" href="">APIE MANE</a></li>
                <li className=""><a className="text-md" href="workpage.html">MANO DARBAI</a></li>
            </ul>

            {/* <!-- mobile menu links here --> */}
            <div id="menu" className={`backdrop:fixed top-0 right-0 ${state}`}
            >
            <ul className="top-0 right-0 w-full h-screen py-16 z-50 font-bold flex flex-col justify-center bg-hero2 bg-left-top bg-no-repeat bg-700 bg-blue bg-blend-luminosity
                md:relative md:flex md:p-0 md:bg-transparent md:flex-row md:space-x-6 md:mt-4 md:w-[120px] md:text-right text-black">

                <li className="md:hidden fixed top-4 flex flex-col w-full justify-between px-4">
                    <a href="javascript:void(0)" className="text-right text-4xl mr-2 self-end -mt-2"
                        onClick={toggle}>&times;</a>
                </li>

            <div className="px-8 -mt-16">
                <h1 className="text-2xl my-3 text-center font-futura">NAVIGUOJAM!</h1>
                <li className="mt-4 py-2 border-4 border-black flex justify-center bg-white"><a className="text-md" href="#">APIE MANE</a></li>
                <li className="mt-4 py-2 border-4 border-black flex justify-center bg-white"><a className="text-md" href="#">MANO DARBAI</a></li>

                <h1 className="text-2xl my-3 text-center pt-4">SUSISIEKIAM!</h1>
                <ul>
                    <li id="email" className="py-2 border-4 border-black mt-4 flex justify-center bg-white">
                        <img className="block ml-0.5 mt-px h-6 bg-[url('../images/email_white.png')]" />
                        <span className="mt-px mx-3">augustinas@augis.lt</span>
                    </li>
                    <li id="phone" className="py-2 border-4 border-black mt-4 flex justify-center bg-white">
                        <img className="block ml-0.5 mt-px h-6 bg-[url('../images/phone-call.png')]" />
                        <span className="mt-px mx-3">+370 678 64 334</span>
                    </li>
                </ul>
            </div>

            </ul>
            </div>
{/* this doesnt work because its not targetting the #menu  */}

        <div className="flex md:hidden items-center z-96">
            <button className="text-white text-4xl font-bold hover:opacity-100 duration-300 mb-3"
                onClick={toggle}>
                &#9776;
            </button>
        </div>

        </div>
    </header>
  );
}

export default Header
