import Header from "./header"
import Hero from "./hero"
import HomePortfolio from "./home_portfolio"
import { useInView, InView } from 'react-intersection-observer';
import { useState } from "react";

const Home = () => {


  const [isHomePortfolioVisible, setIsHomePortfolioVisible] = useState(false);

  const handleIntersection = (inView, entry) => {
    setIsHomePortfolioVisible(inView || entry.intersectionRatio > 0);
    console.log(entry.intersectionRatio);
  };

  return (
    <>
      <Header isVisible={isHomePortfolioVisible} />
      <Hero />
      <InView onChange={handleIntersection} rootMargin="-80px 0px 0px 0px">
        <HomePortfolio />
      </InView>
    </>
  )
}
export default Home
