import Header from "./header"
import Hero from "./hero"
import HomePortfolio from "./home_portfolio"
import { useInView, InView } from 'react-intersection-observer';
import { useState, useRef } from "react";

const Home = () => {
  const [isHomePortfolioVisible, setIsHomePortfolioVisible] = useState(false);
  const homePortfolioRef = useRef(null);

  const handleIntersection = (inView, entry) => {
    setIsHomePortfolioVisible(inView);
  };

  return (
    <>
      <Header isVisible={isHomePortfolioVisible} />
      <Hero />
      <InView onChange={handleIntersection} root={homePortfolioRef.current}>
        <div ref={homePortfolioRef}>
          <HomePortfolio />
        </div>
      </InView>
    </>
  )
}
export default Home
