import './styles/main.css';

import Header from './components/header';
import Hero from './components/hero';
import HomePortfolio from './components/home_portfolio';
import { useState } from 'react';

function App() {


  const [overflow, setOverflow] = useState('hidden')

  const toggleData = (childdata) => {
      setOverflow(childdata);
          //used to toggle the menu button on mobile and open up the navigation
    const [state, setState] = useState("hidden");
    const toggleMenu = () => {
        console.log('menu clicked');
        if (state === 'hidden') {
            setState("w-full h-screen");
        } else {
            setState('hidden');
        }
    }
  }


  return (
    <div className={`bg-purple h-screen overflow-${overflow}`}>
        <Header toggleData={toggleData}/>
        <Hero />
        <HomePortfolio />
    </div>

  );
}

export default App;
