import { useState } from 'react';
import { useInView } from 'react-intersection-observer';
import HomePortfolio from './home_portfolio';

function Home() {
  const [isHeaderIntersecting, setIsHeaderIntersecting] = useState(false);
  const { ref, inView } = useInView({
    threshold: 0,
  });

  const headerStyle = {
    position: isHeaderIntersecting ? 'fixed' : 'relative',
    top: 0,
    left: 0,
    width: '100%',
    backgroundColor: isHeaderIntersecting ? 'black' : 'transparent',
    color: isHeaderIntersecting ? 'white' : 'black',
    padding: '20px',
    zIndex: 100,
  };

  const handleHeaderIntersection = (entries) => {
    setIsHeaderIntersecting(entries[0].isIntersecting);
  };

  return (
    <div>
      <header ref={ref} style={headerStyle}>
        <h1>Header</h1>
      </header>
      <div style={{ height: '1000px' }}></div>
      <HomePortfolio onIntersectionChange={handleHeaderIntersection} />
    </div>
  );
}

export default Home
