import { Works } from '../data';
import { useState, useRef, useEffect, useMemo } from 'react';

import '../styles/main.css';
import '../styles/tailwind.css';


const HomePortfolio = () => {
    const targetRef = useRef(null);
    const [isVisible, setIsVisible] = useState(false);

    const callbackFunction = (entries) => {
        const entry = entries[0];
        setIsVisible(entry.isIntersecting);
    }

    const options = useMemo(() => {
        return {
        root: null,
        rootMargin: '0px',
        threshold: 0.3,
        }
    }, []);

    useEffect(() => {
        const observer = new IntersectionObserver(callbackFunction, options);
        const currentTarget = targetRef;
        if (currentTarget) observer.observe(currentTarget);

        return () => {
            if(currentTarget) observer.unobserve(currentTarget);
        }

    }, [targetRef, options])



  return (

    <section 
    className="flex flex-col justify-center bg-white" 
    data-section="bg-white">
        <h2 className='observer'>{!isVisible ? 'not visible' : 'visible'}</h2>
        <h1 ref={targetRef}
            className="h-8 w-screen text-center text-2xl lg:text-4xl lg:mb-8 mb-4 font-futura">PORTFOLIO</h1>
            <div className="work-display h-2/3 lg:h-1/2 w-auto grid grid-rows-2 grid-cols-2 gap-4 mx-4
            md:mx-20 lg:grid-rows-1 lg:grid-cols-4">
                {Works.map((workItem) => {
                    const { id, img1, title } = workItem

                    // workaround for creating dynamic background images
                    const img = {
                        backgroundImage: 'url(' + img1 + ')',
                    };
                    return (
                        <div 
                        key={id} 
                        className="cursor-pointer border-2 lg:border-4 border-black flex bg-cover bg-top"
                        style={img}>
                    <button className="border-t-2 lg:border-t-4 border-black h-8 lg:h-12 w-full font-bold self-end bg-white text-xs 
                    lg:text-base">{title}</button>
                        </div>
                    );
                })}
            </div>
    </section>

  );
}
export default HomePortfolio
