import HomePortfolio from './home_portfolio';
import Header from './header';
import Hero from './hero';
import { InView, useInView } from 'react-intersection-observer';
import { useState, useRef } from 'react';

function Home() {

  const observerTargerRef = useRef(null);
  const { ref, inView } = useInView({ threshold: 0, ref: observerTargerRef })

  window.addEventListener('scroll', console.log(inView));
  return (
    <>
    <Header ref={ref} inView={inView}/>
    <Hero />
    <HomePortfolio ref={observerTargerRef}/>
    </>
  );
}

export default Home
