import './styles/main.css';

import Header from './components/header';
import Hero from './components/hero';
import HomePortfolio from './components/home_portfolio';
import { useState } from 'react';

function App() {

    //used to toggle the menu button on mobile and open up the navigation
    const [data, setData] = useState('');
    const parentToChild = () => {
      setData("This is data from Parent Component to the Child Component.");
    }

  return (
    <div className={`bg-purple h-screen`}>

        <button type='button' onClick={() => parentToChild()}>Click Parent</button>
        <Header />
        <Hero />
        <HomePortfolio />
    </div>

  );
}

export default App;
