import Header from "./header"
import Hero from "./hero"
import HomePortfolio from "./home_portfolio"
import { useInView } from 'react-intersection-observer';

const Home = () => {


  const handleIntersection = (entries) => {
    if (entries[0].isIntersecting) {
      // The Header is intersecting with the HomePortfolio
      // component, so change its background color here.
    } else {
      // The Header is not intersecting with the HomePortfolio
      // component, so reset its background color here.
    }
  };

  return (
    <>
    <useInView onChange={handleIntersection}>
      <Header />
      <Hero />
      <HomePortfolio />
    </useInView>
    </>
  )
}
export default Home
