import './styles/main.css';

import Header from './components/header';
import Hero from './components/hero';
import HomePortfolio from './components/home_portfolio';
import { useState } from 'react';

function App() {

    //used to toggle the menu button on mobile and open up the navigation
    const [mobileNavIsActive, setData] = useState('');
    const parentToChild = () => {
      setData('hidden');
    }

  return (
    <div className={`bg-purple h-screen`}>

        <button type="button" onClick={() => parentToChild()}>Click Parent</button>
        <Header parentToChild={mobileNavIsActive}/>
        <Hero />
        <HomePortfolio />
    </div>

  );
}

export default App;
