import Header from './header';
import Hero from './hero';

function Home() {
  const [isHeaderVisible, setIsHeaderVisible] = useState(true);

  const handleObserver = (entries) => {
    setIsHeaderVisible(prevIsHeaderVisible => {
      const entry = entries[0];
      return entry.isIntersecting ? false : true;
    });
  };

  const headerStyle = {
    backgroundColor: isHeaderIntersecting ? 'black' : 'transparent',
    color: isHeaderIntersecting ? 'white' : 'black',
  };

  const handleHeaderIntersection = (entries) => {
    setIsHeaderIntersecting(entries[0].isIntersecting);
  };

  return (
    <div>
      <header ref={ref} style={headerStyle}>
        <Header />
        </header>
        <Hero />
      <HomePortfolio onIntersectionChange={handleHeaderIntersection} />
    </div>
  );
}

