import React from "react"
import { useLocation, useParams } from "react-router-dom";
import Header from "../components/header"
import { Works } from "../data";
// import './styles/main.css';


// TO DO:

// make like a carousel thingie for displaying a single workItem. 
// below it you should still see the entire "home_portfolio" compontent, but it should say "See others" instead of "PORTFOLIO"
//

const WorkPage = ( ) => {

    // captures the current URL and converts it into a number
    const itemIDObject = useParams(useLocation());
    const itemID = parseInt(itemIDObject.id - 1);
    // that number is used to capture the array index
    const workItem = Works[itemID];

    const img = {
        backgroundImage: `url(${workItem.img1})`
    };

  return (
    <>
    <Header />
    <section className="h-screen w-screen flex flex-col">
        <div className="h-full w-full">
            <div
            className={`md:last:col-span-1 md:row-span-2 cursor-pointer border-2 lg:border-[3px] border-black flex bg-cover bg-top`}
            style={img}>
                <button className="border-t-2 lg:border-t-[3px] border-black h-8 lg:h-12 w-full font-bold self-end bg-white text-xs 
                    lg:text-base">{workItem.title}</button>
            </div>
        </div>
    </section>
    </>
  )
}
export default WorkPage
